package com.big.openweather.uiimport android.os.Bundleimport androidx.appcompat.app.AppCompatActivityimport com.big.openweather.Rimport com.big.openweather.ui.main.MainFragmentimport dagger.hilt.android.AndroidEntryPoint@AndroidEntryPointclass MainActivity : AppCompatActivity() {    companion object {        const val TAG = "MainActivity"    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        if (savedInstanceState == null) {            supportFragmentManager.beginTransaction()                    .replace(R.id.container, MainFragment.newInstance())                    .commitNow()        }    }}