package com.big.openweather.ui.mainimport androidx.lifecycle.ViewModelimport com.big.openweather.data.Repositoryimport javax.inject.Injectclass MainViewModel @Inject constructor(private val repository: Repository) : ViewModel() {    fun getWeatherReport() = repository.weatherLiveData    fun getSearchResult(query: String) {        repository.getWeatherReport(query)    }    //TODO: local caching is needed here}